// src/pages/DeleteProductPage.js
import React, { useState } from 'react';
import { deleteProduct } from '../services/api';
import './DeleteProductPage.css';

function DeleteProductPage() {
  const [productId, setProductId] = useState('');
  const [message, setMessage] = useState('');

  const handleDelete = async () => {
    if (!productId) {
      setMessage('❌ Product ID cannot be empty');
      return;
    }

    try {
      console.log('Attempting to delete product with ID:', productId);
      await deleteProduct(productId);
      setMessage('✅ Product deleted successfully');
    } catch (err) {
      console.error('Delete failed:', err);
      setMessage('❌ Error: Failed to delete product. Please check if the ID is correct.');
    }
  };

  return (
    <div className="delete-container">
      <h2>Delete Product</h2>
      <input
        className="delete-input"
        type="text"
        placeholder="Enter Product ID"
        value={productId}
        onChange={(e) => setProductId(e.target.value)}
      />
      <button className="delete-button" onClick={handleDelete}>
        Delete
      </button>
      <p className="delete-message">{message}</p>
    </div>
  );
}

export default DeleteProductPage;
