import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { fetchProductById, updateProduct } from '../services/api';
import axios from 'axios';
import './UpdateProductPage.css';

function UpdateProductPage() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [product, setProduct] = useState({
    name: '',
    description: '',
    price: '',
    image: ''
  });

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchProductById(id)
      .then((res) => {
        setProduct(res.data);
        setLoading(false);
      })
      .catch((err) => {
        console.error('Fetch failed:', err);
        setError('❌ Failed to fetch product data');
        setLoading(false);
      });
  }, [id]);

  const handleChange = (e) => {
    setProduct({ ...product, [e.target.name]: e.target.value });
  };

  const handleImageUpload = async (e) => {
    const formData = new FormData();
    formData.append('image', e.target.files[0]);

    try {
      const res = await axios.post('http://localhost:5000/api/products/upload', formData);
      setProduct({ ...product, image: res.data.imageUrl });
    } catch (err) {
      console.error('Upload failed', err);
      setError('❌ Image upload failed.');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await updateProduct(id, product);
      navigate('/products');
    } catch (err) {
      console.error('Update failed:', err);
      setError('❌ Update failed. Please try again.');
    }
  };

  if (loading) return <div>Loading product...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="update-container">
      <h2>Update Product</h2>
      <form onSubmit={handleSubmit} className="update-form">
        <input
          name="name"
          placeholder="Product Name"
          value={product.name}
          onChange={handleChange}
          required
        />
        <textarea
          name="description"
          placeholder="Product Description"
          value={product.description}
          onChange={handleChange}
          required
        />
        <input
          name="price"
          type="number"
          placeholder="Price"
          value={product.price}
          onChange={handleChange}
          required
        />
        <input type="file" onChange={handleImageUpload} accept="image/*" />
        {product.image && (
          <img src={product.image} alt="Preview" className="preview-image" />
        )}
        <button type="submit">Update Product</button>
      </form>
    </div>
  );
}

export default UpdateProductPage;
