import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { fetchProductById, updateProduct } from '../services/api';
import axios from 'axios';
import './UpdateProductPage.css';

function UpdateProductPage() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [product, setProduct] = useState({
    name: '',
    description: '',
    price: '',
    image: ''
  });

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // Ëé∑Âèñ‰∫ßÂìÅÊï∞ÊçÆ
  useEffect(() => {
    fetchProductById(id)
      .then((res) => {
        setProduct(res.data);
        setLoading(false);
      })
      .catch((err) => {
        console.error('Fetch failed:', err);
        setError('‚ùå Failed to fetch product data');
        setLoading(false);
      });
  }, [id]);

  // ËæìÂÖ•Ê°ÜÂèòÊõ¥Â§ÑÁêÜ
  const handleChange = (e) => {
    setProduct({ ...product, [e.target.name]: e.target.value });
  };

  // ÂõæÁâá‰∏ä‰º†Â§ÑÁêÜ
  const handleImageUpload = async (e) => {
    const formData = new FormData();
    formData.append('image', e.target.files[0]);

    try {
      const res = await axios.post('http://localhost:5000/api/products/upload', formData);
      setProduct((prev) => ({ ...prev, image: res.data.imageUrl })); // ‚úÖ Êé®ËçêÁî®ÂáΩÊï∞ÂºèÂÜôÊ≥ï
    } catch (err) {
      console.error('Upload failed', err);
      setError('‚ùå Image upload failed.');
    }
  };

  // Ë°®ÂçïÊèê‰∫§Â§ÑÁêÜ
  const handleSubmit = async (e) => {
    e.preventDefault();

    console.log('üìù Updating product with data:', product); // ‚úÖ Ë∞ÉËØïÁî®

    try {
      await updateProduct(id, product);
      navigate('/products');
    } catch (err) {
      console.error('Update failed:', err);
      setError('‚ùå Update failed. Please try again.');
    }
  };

  if (loading) return <div>Loading product...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="update-container">
      <h2>Update Product</h2>
      <form onSubmit={handleSubmit} className="update-form">
        <input
          name="name"
          placeholder="Product Name"
          value={product.name}
          onChange={handleChange}
          required
        />
        <textarea
          name="description"
          placeholder="Product Description"
          value={product.description}
          onChange={handleChange}
          required
        />
        <input
          name="price"
          type="number"
          placeholder="Price"
          value={product.price}
          onChange={handleChange}
          required
        />
        <input type="file" onChange={handleImageUpload} accept="image/*" />

        {/* ‚úÖ ÂõæÁâáÈ¢ÑËßàÔºåÊãºÊé•ÂÆåÊï¥ URL */}
        {product.image && (
          <img
            src={`http://localhost:5000${product.image}`}
            alt="Preview"
            className="preview-image"
          />
        )}

        <button type="submit">Update Product</button>
      </form>
    </div>
  );
}

export default UpdateProductPage;
