import React, { useState, useEffect } from 'react';

function ProductsPage() {
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState('');
  const [editProduct, setEditProduct] = useState(null);

  useEffect(() => {
    fetch('/api/products')
      .then((res) => res.json())
      .then((data) => setProducts(data));
  }, []);

  const handleCreate = () => {
    fetch('/api/products', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ name: newProduct }),
    })
      .then((res) => res.json())
      .then((product) => setProducts([...products, product]));
  };

  const handleUpdate = (id) => {
    fetch(`/api/products/${id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ name: editProduct }),
    })
      .then((res) => res.json())
      .then((updatedProduct) =>
        setProducts(products.map((p) => (p.id === id ? updatedProduct : p)))
      );
  };

  const handleDelete = (id) => {
    fetch(`/api/products/${id}`, { method: 'DELETE' }).then(() =>
      setProducts(products.filter((p) => p.id !== id))
    );
  };

  return (
    <div>
      <h1>Products</h1>
      <input
        type="text"
        value={newProduct}
        onChange={(e) => setNewProduct(e.target.value)}
        placeholder="New Product"
      />
      <button onClick={handleCreate}>Add Product</button>
      <ul>
        {products.map((product) => (
          <li key={product.id}>
            {product.name}
            <button onClick={() => setEditProduct(product.name)}>Edit</button>
            <button onClick={() => handleDelete(product.id)}>Delete</button>
            {editProduct && (
              <div>
                <input
                  type="text"
                  value={editProduct}
                  onChange={(e) => setEditProduct(e.target.value)}
                />
                <button onClick={() => handleUpdate(product.id)}>Save</button>
              </div>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ProductsPage;
