// src/pages/ProductCreatePage.js
import React, { useState } from 'react';
import { createProduct } from '../services/api';
import { useNavigate } from 'react-router-dom';

function ProductCreatePage() {
  const [form, setForm] = useState({
    name: '',
    description: '',
    price: '',
    image: ''
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await createProduct(form);
      navigate('/products');
    } catch (err) {
      console.error('Error creating product:', err);
    }
  };

  return (
    <div style={{ maxWidth: '600px', margin: '30px auto' }}>
      <h2>Create Product</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" name="name" placeholder="Name" value={form.name} onChange={handleChange} required /><br />
        <textarea name="description" placeholder="Description" value={form.description} onChange={handleChange} required /><br />
        <input type="number" name="price" placeholder="Price" value={form.price} onChange={handleChange} required /><br />
        <input type="text" name="image" placeholder="Image URL" value={form.image} onChange={handleChange} /><br />
        <button type="submit">Create</button>
      </form>
    </div>
  );
}

export default ProductCreatePage;
