import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { fetchProductById } from '../services/api';
import './ProductPage.css';

function ProductPage() {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [error, setError] = useState(null);

  // 根据产品 ID 获取产品数据
  useEffect(() => {
    if (!id) {
      setError('未提供产品 ID');
      return;
    }
    fetchProductById(id)
      .then((res) => {
        console.log('Fetched product data:', res.data);
        setProduct(res.data);
      })
      .catch((err) => {
        console.error('Error fetching product:', err);
        setError('获取产品数据出错');
      });
  }, [id]);

  if (error) {
    return <div>{error}</div>;
  }

  if (!product) {
    return <div>Loading...</div>;
  }

  // 如果产品数据中提供了图片路径，则进行标准化处理，否则使用备用占位图
  let fullImageUrl = '';
  if (typeof product.image === 'string' && product.image.trim() !== '') {
    // 如果路径不以斜杠开头，则自动添加一个
    const normalizedImage = product.image.startsWith('/')
      ? product.image
      : '/' + product.image;
    fullImageUrl = `http://localhost:5000${normalizedImage}`;
  } else {
    
    fullImageUrl = 'https://placehold.co/350x200?text=No+Image';
  }

  console.log('Product ID:', id);
  console.log('Full image URL:', fullImageUrl);

  return (
    <div className="product-page">
      {fullImageUrl && (
        <img
          src={fullImageUrl}
          alt={product.name}
          className="product-image"
        />
      )}
      <h2>{product.name}</h2>
      <p>
        <strong>Product Number:</strong> {product._id}
      </p>
      <p>{product.description}</p>
      <p>
        <strong>Price:</strong> ${product.price}
      </p>
    </div>
  );
}

export default ProductPage;
