import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './UpdateProductPage.css'; // Import the CSS file

function UpdateProductPage() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState({ name: '', price: '', description: '' });
  const [message, setMessage] = useState(''); // Feedback message
  const [loading, setLoading] = useState(false); // Loading state

  useEffect(() => {
    // Fetch product details by ID
    const fetchProduct = async () => {
      setLoading(true);
      try {
        const response = await fetch(`http://localhost:5000/api/products/${id}`);
        if (response.ok) {
          const data = await response.json();
          setProduct(data);
        } else {
          setMessage('Failed to fetch product details.');
        }
      } catch (error) {
        setMessage('An error occurred while fetching product details.');
        console.error('Error:', error);
      } finally {
        setLoading(false);
      }
    };
    fetchProduct();
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProduct({ ...product, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await fetch(`http://localhost:5000/api/products/${id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(product),
      });
      if (response.ok) {
        setMessage('Product updated successfully!');
        navigate('/products'); // Redirect to products page
      } else {
        const errorData = await response.json();
        setMessage(errorData.message || 'Failed to update product.');
      }
    } catch (error) {
      setMessage('An error occurred while updating the product.');
      console.error('Error:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="update-product-page">
      <h1 className="page-title">Update Product</h1>
      {loading && <p className="loading-message">Loading...</p>}
      {message && <p className="feedback-message">{message}</p>}
      <form className="product-form" onSubmit={handleSubmit}>
        <input
          className="form-input"
          type="text"
          name="name"
          placeholder="Product Name"
          value={product.name}
          onChange={handleChange}
        />
        <input
          className="form-input"
          type="number"
          name="price"
          placeholder="Product Price"
          value={product.price}
          onChange={handleChange}
        />
        <textarea
          className="form-textarea"
          name="description"
          placeholder="Product Description"
          value={product.description}
          onChange={handleChange}
        />
        <button className="form-button" type="submit" disabled={loading}>
          Update
        </button>
      </form>
    </div>
  );
}

export default UpdateProductPage;
