import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { createProduct } from '../services/api';
import axios from 'axios';
import './CreateProductPage.css';

function CreateProductPage() {
  const [product, setProduct] = useState({
    name: '',
    description: '',
    price: '',
    image: ''
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    setProduct({ ...product, [e.target.name]: e.target.value });
  };

  const handleImageUpload = async (e) => {
    const formData = new FormData();
    formData.append('image', e.target.files[0]);

    try {
      const res = await axios.post('http://localhost:5000/api/products/upload', formData);
      setProduct(prev => ({ ...prev, image: res.data.imageUrl })); // ✅ 使用 prev 形式更安全
    } catch (err) {
      console.error('Upload failed', err);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await createProduct(product);
      navigate('/products');
    } catch (err) {
      console.error('Failed to create product:', err);
    }
  };

  return (
    <div className="create-container">
      <h2>Create Product</h2>
      <form onSubmit={handleSubmit} className="create-form">
        <input name="name" placeholder="Product Name" value={product.name} onChange={handleChange} required />
        <textarea name="description" placeholder="Product Description" value={product.description} onChange={handleChange} required />
        <input name="price" type="number" placeholder="Price" value={product.price} onChange={handleChange} required />
        <input type="file" onChange={handleImageUpload} accept="image/*" />

        {/* ✅ 正确预览图片（拼接完整路径） */}
        {product.image && (
          <img
            src={`http://localhost:5000${product.image}`}
            alt="Preview"
            className="preview-image"
          />
        )}

        <button type="submit">Create Product</button>
      </form>
    </div>
  );
}

export default CreateProductPage;
