import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { fetchProductById } from '../services/api';
import './ProductPage.css';

function ProductPage() {
  const { id } = useParams();
  const [product, setProduct] = useState(null);

  // 根据产品 ID 获取产品数据，并打印调试信息
  useEffect(() => {
    fetchProductById(id)
      .then((res) => {
        console.log('Fetched product data:', res.data);
        setProduct(res.data);
      })
      .catch((err) => console.error('Error fetching product:', err));
  }, [id]);

  if (!product) {
    return <div>Loading...</div>;
  }

  // 根据返回的产品图片路径判断并生成完整的图片 URL
  let fullImageUrl = '';
  if (typeof product.image === 'string' && product.image.startsWith('/uploads/')) {
    fullImageUrl = `http://localhost:5000${product.image}`;
  } else if (product.image) {
    fullImageUrl = product.image;
  } else {
    // 如果没有图片，使用一个占位图
    fullImageUrl = 'https://via.placeholder.com/350x200?text=No+Image';
  }

  // 打印生成的图片 URL 以便调试
  console.log('Full image URL:', fullImageUrl);

  return (
    <div className="product-page">
      {fullImageUrl && (
        <img
          src={fullImageUrl}
          alt={product.name}
          className="product-image"
        />
      )}
      <h2>{product.name}</h2>
      <p>
        <strong>Product Number:</strong> {product._id}
      </p>
      <p>{product.description}</p>
      <p>
        <strong>Price:</strong> ${product.price}
      </p>
    </div>
  );
}

export default ProductPage;
