  const handleDelete = async () => {
    if (!productId.trim()) {
      setStatus('‚ùå ËØ∑ËæìÂÖ•‰∫ßÂìÅ ID');
      return;
    }

    try {
      setStatus('üîÑ Ê≠£Âú®Âà†Èô§...');
      await deleteProduct(productId);
      setStatus('‚úÖ ‰∫ßÂìÅÂà†Èô§ÊàêÂäü');
      setProductId('');
    } catch (err) {
      console.error(err);
