// src/pages/DeleteProductPage.js
import React, { useState } from 'react';
import { deleteProduct } from '../services/api';
import { useNavigate } from 'react-router-dom';
import './DeleteProductPage.css';

function DeleteProductPage() {
  const [name, setName] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleDelete = async () => {
    try {
      await deleteProductByName(name);
      setMessage(`✅ 产品 "${name}" 删除成功`);
      setTimeout(() => navigate('/products'), 1500);
    } catch (err) {
      setMessage(`❌ 错误: ${err.response?.data?.message || '删除失败，请检查产品名是否正确'}`);
    }
  };

  return (
    <div className="delete-container">
      <h2>删除产品</h2>
      <input
        type="text"
        placeholder="输入产品名称"
        value={name}
        onChange={(e) => setName(e.target.value)}
        className="delete-input"
      />
      <button onClick={handleDelete} className="delete-button">删除</button>
      {message && <p className="delete-message">{message}</p>}
    </div>
  );
}

export default DeleteProductPage;
