import axios from 'axios';

const API = axios.create({
  baseURL: 'http://localhost:5000/api', 
});


export const fetchProducts = () => {
  return Promise.resolve({
    data: [
      {
        id: 1,
        name: 'Smartphone',
        description: 'A high-end smartphone with a stunning display and powerful processor.',
        price: 999,
        image: '/images/smartphone.jpg',
      },
      {
        id: 2,
        name: 'Laptop',
        description: 'A lightweight laptop with excellent performance and long battery life.',
        price: 1299,
        image: '/images/laptop.jpg',
      },
      {
        id: 3,
        name: 'Smartwatch',
        description: 'A stylish smartwatch with fitness tracking and notification features.',
        price: 299,
        image: '/images/smartwatch.jpg',
      },
    ],
  });
};

export const fetchProductById = (id) => {
  const products = [
    {
      id: 1,
      name: 'Smartphone',
      description: 'A high-end smartphone with a stunning display and powerful processor.',
      price: 999,
      image: '/images/smartphone.jpg',
    },
    {
      id: 2,
      name: 'Laptop',
      description: 'A lightweight laptop with excellent performance and long battery life.',
      price: 1299,
      image: '/images/laptop.jpg',
    },
    {
      id: 3,
      name: 'Smartwatch',
      description: 'A stylish smartwatch with fitness tracking and notification features.',
      price: 299,
      image: '/images/smartwatch.jpg',
    },
  ];
  return Promise.resolve({ data: products.find((product) => product.id === id) });
};
